function switch_language(){document.querySelector(".ua_switcher").classList.add("disabled");for(const e of document.querySelectorAll(".lang"))e.textContent=ua_lang_dict[e.attributes.key.nodeValue]}import{ua_lang_dict}from"./translation_into_ua.js";let switcher_class;if(console.log("switch_language.min.js is loaded"),"ua"==localStorage.getItem("language")||"ua"==search_params.get("language"))switch_language(),switcher_class="en";else{switcher_class="ua";const e=document.querySelector(".en_switcher");e.classList.add("disabled")}const switch_language_btn=document.querySelector(`.${switcher_class}_switcher`);switch_language_btn.addEventListener("click",function(){"ua"==localStorage.getItem("language")?localStorage.setItem("language","en"):localStorage.setItem("language","ua"),window.history.replaceState(null,null,window.location.pathname),location.reload()});