import{ua_lang_dict}from"./translation_into_ua.min.js";function switch_language(){document.querySelector(".ua_switcher").classList.add("disabled");for(const e of document.querySelectorAll(".lang"))e.textContent=ua_lang_dict[e.attributes.key.nodeValue]}let switcher_class;if("ua"==localStorage.getItem("language"))switch_language(),switcher_class="en";else{switcher_class="ua";const e=document.querySelector(".en_switcher");e.classList.add("disabled")}const switch_language_btn=document.querySelector(`.${switcher_class}_switcher`);switch_language_btn.addEventListener("click",function(){"ua"==localStorage.getItem("language")?localStorage.setItem("language","en"):localStorage.setItem("language","ua"),location.reload()});