function set_dark_mode(){localStorage.setItem("dark-theme","yes"),document.documentElement.setAttribute("theme","dark")}const theme_toggler_btn=document.getElementById("theme_toggler_btn");theme_toggler_btn.addEventListener("change",()=>{document.documentElement.hasAttribute("theme")?(localStorage.setItem("dark-theme","no"),document.documentElement.removeAttribute("theme")):set_dark_mode()}),"yes"==localStorage.getItem("dark-theme")&&(set_dark_mode(),theme_toggler_btn.checked=!theme_toggler_btn.checked);